import { Component, OnInit } from '@angular/core';

@Component({
  selector: 'app-email',
  templateUrl: './email.component.html',
  styleUrls: ['./email.component.css']
})
export class EmailComponent implements OnInit
{
  constructor(){

  }
  ngOnInit(): void {

  }
  doSubmitForm(){
    console.log("try to submit form");

  }

}
<div class="container">
    <div class="card">
        <form (ngSubmit)="doSubmitForm">
            <h1>Compose Email</h1>
            <mat-form-field class="field" appearance="outline">
                <mat-label>To</mat-label>
                <input matInput>
            </mat-form-field>   
            <mat-form-field class="field"appearance="outline">
                <mat-label>Subject</mat-label>
                <input matInput>
            </mat-form-field>
            <mat-form-field class="example-full-width field">
                <mat-label>Enter your message</mat-label>
                <textarea matInput placeholder="Requested For leave "></textarea>
            </mat-form-field>
            <div class="text-center">
                <button type="submit" mat-raised-button color="primary">Submit</button>
                <button type="reset" mat-raised-button color="accent">Reset</button>
            </div>

        </form>

    </div>

</div>
